mapper.select:
select * from t_rental


        and user_id = #{userId}


        and house_id = #{houseId}


        and status = #{status}


order by id desc

mapper.update:
update t_rental
user_id = #{userId}, house_id = #{houseId}, start_date = #{startDate}, end_date = #{endDate}, total_price = #{totalPrice}, status = #{status}, remark = #{remark}, updated_at = #{updatedAt},
where id = #{id}
